import { useEffect, useState } from "react";

function App() {

    const [loading, setLoading] = useState(true);
    const [movies, setMovies] = useState([]);
    const getMovies = async () => {
        const json = await (
            await fetch(
                'https://yts.mx/api/v2/list_movies.json?minimum_rating=8.5'
            )
        ).json();
        setMovies(json.data.movies);
        setLoading(false);
    }

    useEffect(() => {
       getMovies();
    } , []);

    console.log(movies);

    return (
      <div>
        {loading ? 
            <h1>Loading...</h1> : 
            <div>
                {movies.map(movie => 
                    <div key={movie.id}>
                        <h2>{movie.title}</h2>
                        <p>{movie.summary}</p>
                        <ul>
                            {movie.genres.map((g,index) =>
                                <li key={index}>{g}</li>
                            )}
                        </ul>
                    </div>)}
            </div>
        }
      </div>
    );
  }
  
  export default App;
